const buyButton = document.querySelectorAll(".goods-buy"), messagePopup = document.querySelector(".modal-cart"), messageClose = messagePopup.querySelector(".close-cart"); buyButton.forEach(e => { e.addEventListener("click", function (e) { e.preventDefault(), messagePopup.classList.add("modal-show") }) }), messageClose.addEventListener("click", function (e) { e.preventDefault(), messagePopup.classList.remove("modal-show") }), window.addEventListener("keydown", function (e) { 27 === e.keyCode && messagePopup.classList.contains("modal-show") && (e.preventDefault(), messagePopup.classList.remove("modal-show")) });